import React from "react";
import {connect} from "react-redux";
import {fetchUser} from "../actions";

class UserHeader extends React.Component {
    componentDidMount() {
        this.props.fetchUser(this.props.userId); //the fetchUser exist in props because we wire it below on the connect function
    }

    render() {
        const user = this.props.users.find((user) => user.id === this.props.userId) //find a user inside the array

        if (!user) {
            return null;
        }
        return (
            <div className="header">
                {user.name}
            </div>
        );
    }

}

const mapStateToProps = (state) => {
    return {users: state.users};
}

export default connect(
    mapStateToProps,
    {fetchUser}
)(UserHeader);
